import { AuthorizedHttpClient } from '../utils/api-request';
import { ICatalystJobDetails, TCatalystJobs } from './types';
export default class Job {
    requester: AuthorizedHttpClient;
    constructor(requester: AuthorizedHttpClient);
    /**
     * Get a job's details from the jobpool
     * @param id Id of the job
     * @returns details of the job
     */
    getJob<T extends TCatalystJobs>(jobId: string): Promise<ICatalystJobDetails<T>>;
    /**
     * Submit a job to the jobpool
     * @param jobMeta details of the job to be submitted to the jobpool
     * @returns details of the submitted job
     */
    submitJob<T extends TCatalystJobs>(jobMeta: T): Promise<ICatalystJobDetails<T>>;
    /**
     * Delete a job from the job pool
     * @param jobId Id of the job to be deleted
     * @returns details of the deleted job
     */
    deleteJob<T extends TCatalystJobs>(jobId: string): Promise<ICatalystJobDetails<T>>;
}
